  args: {
    transactionId: v.id("transactions"),
  },
  handler: async (ctx, args) => {
    const buyerRelation = await ctx.db
      .query("userBuyerTransactions")
      .withIndex("by_transaction", (q) =>
        q.eq("transactionId", args.transactionId),
      )
      .unique();

    if (!buyerRelation) {
